import styled from 'styled-components';

export const ProductDetailsWrapperStyled = styled.div`
  width: 100%;
  position: relative;
  padding: 25px 20px 20px;

  & .title {
    font-size: 18px;
    font-weight: bold;
    line-height: 21px;
  }
`;

export const ProductCategoryWrapper = styled.div`
  margin: 5px 0;

  & .categoryTag {
    display: inline-block;
    font-size: 12px;
    padding: 5px 8px;
    background-color: #333;
    color: white;
    border-radius: 5px;
    margin-right: 5px;
    cursor: pointer;
    margin-top: 5px;
  }
`;

export const ProductPrice = styled.div`
  margin: 15px 0 10px;
  font-size: 16px;
  color: #999;
  font-style: italic;

  & .main {
    font-size: 30px;
    font-weight: bold;
    color: #333;
    font-style: normal;
    margin-right: 5px;
  }
`;

export const ExchangeRateDate = styled.p`
  font-size: 12px;
  color: #999;
  font-style: italic;

  & .main {
    font-size: 12px;
    color: #666;
    margin-right: 5px;
    font-style: normal;
    font-weight: bold;
  }
`;

export const ShipPrice = styled.p`
  font-size: 12px;
  color: #999;
  margin: 5px 0 0;

  & .main {
    color: #999;
    font-weight: bold;
    margin-left: 5px;
  }
`;

export const ProductButtonWrapper = styled.div`
  margin: 15px 0 10px;
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-top: 1px solid #eee;
  padding: 15px 0 0;

  & button {
    border-radius: 5px;
    background-color: #eee;
    height: 40px;
    font-weight: bold;
    font-size: 14px;
    color: #666;
    border: none;
    display: flex;
    justify-content: center;
    align-items: center;
    outline: none;
    cursor: pointer;
  }
  & img {
    margin-right: 3px;
  }

  & .productHeartButton {
    width: 65%;
    margin-right: 10px;
  }

  & .productShareButton {
    width: 35%;
  }
`;
